<?php

/**
*@Entity 
*@Table(name="users")
*/
class User
{
	/**
	*@Id @Column(type="integer") @GeneratedValue
	*/

	protected $id;

    /**
    *@Column (type="string")
    */

    protected $name;

    /**
    *@Column (type="string")
    */

    protected $address;



    /**
     * Get id
     *
     * @return integer 
     */
    public function getId()
    {
        return $this->id;
    }

    /**
     * Set name
     *
     * @param string $name
     * @return User
     */
    public function setName($name)
    {
        $this->name = $name;
    
        return $this;
    }

    /**
     * Get name
     *
     * @return string 
     */
    public function getName()
    {
        return $this->name;
    }

    /**
     * Set address
     *
     * @param string $address
     * @return User
     */
    public function setAddress($address)
    {
        $this->address = $address;
    
        return $this;
    }

    /**
     * Get address
     *
     * @return string 
     */
    public function getAddress()
    {
        return $this->address;
    }
}